public class CombinationOp_1 {
    public static void main (String []args){
        int a=20;
        int b=30;
        int c=10;
        int d=50;
        a+=10;
        a-=20;
        b-=20;
        c*=4;
        d/=5;
        System.out.println(a);
        System.out.println(b);
        System.out.println(c);
        System.out.println(d);
    }
    
}
