public class incrementOp_3 {
    public static void main (String[]args){
        int a=10;
        int b=++a;
        int c=1+b++;
        int d=1+c++;
        int e=++d+1;
        System.out.println(a);
        System.out.println(b);
        System.out.println(c);
        System.out.println(d);
        System.out.println(e);
    }
    
}
